}

mutation createArtist ($firstName: String, $secondName: String, $middleName: String, $birthDate: String, $birthPlace: String, $country: String, $bandsIds: [ID], $instruments: [String]) {
    createArtist (firstName: $firstName, secondName: $secondName, middleName: $middleName, birthDate: $birthDate, birthPlace: $birthPlace, country: $country, bandsIds: $bandsIds, instruments: $instruments) {
        id
        firstName
        secondName
        middleName
        birthDate
        birthPlace
        country
        bands {
            id
            name
            origin
            members {
                id
                firstName
                secondName
                middleName
                instrument
                years
            }
            website
            genres {
                id
                name
                description
                country
                year
            }
        }
        instruments
    }
}

